{ d \bar{T} } &=            _1 - ( 1-            _1            _ 1 -            _1            \\            _2 }
{ d \bar{T} } & = -            _2 + ( 1 -            _2 )            _2            _2 +            _2            _2 +            _1 ,            \\            _2 }
